mapper.update:
update PAYMENT
set SETUP_MENU_YN = 'Y'
where FRCS_NO = #{crrFrcsNo}
  and PYNM_NO = #{pynmNo}

mapper.select:
select p.PYNM_NO, PYNM_DT, PYNM_TOTAL_AMT, SETUP_MENU_YN, PYNM_CNT, MENU_NM, MENU_PRICE, ATCH_FILE_NO, MENU_SIZE, MENU_ICE_YN from PAYMENT p, PAYMENT_DETAIL pd where p.PYNM_NO = pd.PYNM_NO and FRCS_NO = ${crrFrcsNo} and SETUP_MENU_YN = 'N'
and p.PYNM_NO = ${pynmNo}
order by PYNM_DT
