mapper.select:
SELECT fo.FRCS_OWNR_NO, fo.FRCS_OWNR_EMAIL, fo.FRCS_OWNR_PW, fo.FRCS_OWNR_NM, fo.FRCS_OWNR_BRDT, fo.FRCS_OWNR_TEL, fo.FRCS_OWNR_ZIP, fo.FRCS_OWNR_ADDR, fo.FRCS_OWNR_DTL_ADDR, fo.FRCS_OWNR_JOIN_DT, fo.ENABLED, fa.FRCS_AUTH_CD, fr.FRCS_NO, fr.FRCS_NM, fr.FRCS_STS_CD, fr.FRCS_RGST_DT, fr.FRCS_END_DT, fr.FRCS_POST_NO, fr.FRCS_ADDR, fr.FRCS_DTL_ADDR, fr.FRCS_BSNS_STRT_TM, fr.FRCS_BSNS_END_TM, fr.FRCS_CTRT_CNCLS_DT, fr.FRCS_EXPT_CMPL_DT, fr.FRCS_FNL_INSP_DT, fr.FRCS_OPNG_DT, fr.FRCS_CPCT_PSN, fr.FRCS_REGION_CD, fr.FRCS_TEL FROM FRCS_OWNER fo JOIN FRCS_AUTH fa ON fo.FRCS_OWNR_NO = fa.FRCS_OWNR_NO JOIN FRANCHISE fr ON fo.FRCS_OWNR_NO = fr.FRCS_OWNR_NO WHERE fo.ENABLED = '1' AND fo.FRCS_OWNR_EMAIL = #{frcsOwnrEmail}
AND fr.FRCS_NO = #{frcsNo}
ORDER BY FR.FRCS_RGST_DT
