mapper.insert:

	SELECT OFFICIAL_DOCUMENT_SEQ.NEXTVAL AS OFF_DOC_NO FROM DUAL

INSERT INTO OFFICIAL_DOCUMENT (
	  OFF_DOC_NO
	, OFF_DOC_FROM
	, OFF_DOC_TO
	, OFF_DOC_TITLE
	, OFF_DOC_CONTS
	, OFF_DOC_FWD_DT
	) VALUES (
	  #{offDocNo}
	, #{offDocFrom}
	, #{offDocTo}
	, #{offDocTitle}
	, #{offDocConts}
	, SYSDATE
	)
