mapper.insert:
INSERT INTO PRODUCT (
      PRDCT_NO
    , PRDCT_CLSF_CD
    , PRDCT_NM
    , PRDCT_RCVN_AMT
    , PRDCT_EXPLN
    , PRDCT_PRCHS_STRT_DT
    , PRDCT_PRCHS_END_DT
    , PRDCT_DSCN_YN
    , ALLERGENS_YN
    , HQ_INVEN_CNT
    , HQ_INVEN_PROPER_CNT

    , ACNT_NO

    , ATCH_FILE_NO
) VALUES (
    PRODUCT_SEQ.nextVal
  , #{prdctClsfCd}
  , #{prdctNm}
  , #{prdctRcvnAmt}
  , #{prdctExpln}
  , #{prdctPrchsStrtDt}
  , #{prdctPrchsEndDt}
  , #{prdctDscnYn}
  , #{allergensYn}
  , #{hqInvenCnt}
  , #{hqInvenProperCnt}

  , #{acntNo}

  , #{atchFileNo}
)

mapper.select:
SELECT COUNT(*)
FROM PRODUCT
WHERE PRDCT_NM = #{prdctNm}
