mapper.select:
select fod.FRCS_ORDER_NO, fod.FRCS_NO, fod.prdct_nm, fod.frcs_order_qy, p.prdct_rcvn_amt, fo.frcs_order_order_amt, fo.frcs_order_dt
from frcs_order_detail fod, product p, frcs_order fo where fod.prdct_nm = p.prdct_nm and fod.frcs_order_no = fo.frcs_order_no and fod.frcs_no = fo.frcs_no
and fo.frcs_order_dt BETWEEN TO_DATE('2023-12-01', 'YYYY-MM-DD') AND TO_DATE('2024-12-31', 'YYYY-MM-DD') and TO_CHAR(fo.frcs_order_dt, 'yyyy-MM') = #{viewDate} and fod.frcs_no = #{frcsNo}

select DISTINCT fod.FRCS_ORDER_NO, fod.FRCS_NO, fod.prdct_nm, fod.frcs_order_qy
from frcs_order_detail fod, product p, frcs_order fo where fod.prdct_nm = p.prdct_nm and fod.frcs_order_no = fo.frcs_order_no and fod.frcs_no = fo.frcs_no
and fo.frcs_order_dt BETWEEN TO_DATE('2023-12-01', 'YYYY-MM-DD') AND TO_DATE('2024-12-31', 'YYYY-MM-DD') and TO_CHAR(fo.frcs_order_dt, 'yyyy-MM') = #{viewDate} and fod.frcs_no = #{frcsNo}
